from setuptools import setup, find_packages

VERSION = '0.0.1'
DESCRIPTION = 'texNet: A machine learning model to predict roughness in images.'

# Setting up
setup(
    name="texnet",
    version=VERSION,
    author="David Beattie",
    author_email="david.beattie@ultraleap.com",
    description=DESCRIPTION,
    packages=find_packages(where="texnet",
                           exclude=('.git', '.idea',
                                    'build', 'dist')),
    install_requires=[
	    'numpy',
		'keras',
		'pandas',
		'scikit-image',
		'matplotlib'],
    keywords=['texnet', 'texture', 'haptics'],
    classifiers=[
        "Development Status :: 3 - Alpha",
        "Intended Audience :: Research",
        "Programming Language :: Python :: 3",
        "Operating System :: MacOS :: MacOS X",
        "Operating System :: Microsoft :: Windows",
    ],
    package_dir={"texnet": "texnet"},
    license_files = ('LICENSE.txt',),
    include_package_data = True
)